<?php/* index.php */    require('rotation.php');    class PDF extends PDF_Rotate{            protected $_outerText1;// dynamic text        protected $_outerText2;        function setWaterText($txt1="", $txt2=""){            $this->_outerText1 = $txt1;            $this->_outerText2 = $txt2;        }        function Header(){            //Put the watermark            $this->SetFont('Arial','B',12);            $this->SetTextColor(255,192,203);                    $this->SetAlpha(0.5);			$widthstep = 100 / log(strlen($this->_outerText1));			$heightstep = $widthstep;			for ($i=0; $i<20; $i++) {				for ($j=0; $j<20; $j++) {					$this->RotatedText($widthstep / 2 * ($j % 2) + $widthstep * $i,	0 + $heightstep * $j, $this->_outerText1, 45);				}			}            //$this->RotatedText(75,190, $this->_outerText2, 45);        }        function RotatedText($x, $y, $txt, $angle){            //Text rotated around its origin            $this->Rotate($angle,$x,$y);            $this->Text($x,$y,$txt);            $this->Rotate(0);        }    }	$file = $_FILES["pdfile"];	if (!(isset($file))) {		echo "NO PDF FILE<br/><a href='index.php'>Back</a>";		exit;	}	$target_Path = "images/";	$target_Path = $target_Path.basename( $file['name'] );	move_uploaded_file( $file['tmp_name'], $target_Path );    $file = $target_Path;    $pdf = new PDF();    if (file_exists($file)){        $pagecount = $pdf->setSourceFile($file);    } else {        return FALSE;    }   $pdf->setWaterText($_POST["watermark"]);  /* loop for multipage pdf */   for($i=1; $i <= $pagecount; $i++) {      $tpl = $pdf->importPage($i);                    $pdf->addPage();      $pdf->useTemplate($tpl, 1, 1, 0, 0, TRUE);     }   $pdf->Output(); //specify path filename to save or keep as it is to view in browser?>